.elemento1 {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-top: 35%;
    margin-bottom: 49%;
}

/* Colors */

:root {
    --bttwitch-bg-primary: rgb(184.24, 23.67, 210.37);
    --bttwitch-bg-secondary: rgb(98.82, 58.13, 214.62);
    --bttwitch-bg-tertiary: rgb(149.07, 72.39, 209.31);
    --bttwitch-box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.5);
}

/* Button styles */

.button9 {
    width: 170px;
    height: 60px;
    border-radius: 10px;
    border: none;
    font-weight: 600;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--bttwitch-box-shadow);
    cursor: pointer;
    transition-duration: .3s;
    overflow: hidden;
    position: relative;
}

.button9 svg {
    width: 100%;
    height: 100%;
    object-fit: scale-down;
    transition: transform 0.3s;
}

.button9:hover svg {
    transform: translateX(-40px) scale(0.9);
}

/* Button colors */

.button9 {
    background: linear-gradient(140deg, var(--bttwitch-bg-primary) 0%, var(--bttwitch-bg-secondary) 53.5%, var(--bttwitch-bg-tertiary) 100%);
}

/* Nametag */

.button9::before {
    position: absolute;
    top: -20px;
    /* ------NAME or Nametag | Twitch ---------- */
    content: "@Namehere";
    /* -End-----NAME or Nametag | Twitch ---------- */
    color: white;
    transition-duration: .3s;
    font-size: 12px;
    /* Reduce the size of the text Depending on the size of the name */
    left: 50%;
    transform: translateX(-50%);
    white-space: nowrap;
}

.button9:hover::before {
    white-space: pre-wrap;
    opacity: 1;
    transform: translateY(30px);
}

.elemento2 {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-top: 35%;
    margin-bottom: 53%;
}

.Btn {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    width: 160px;
    height: 40px;
    border: none;
    padding: 0px 20px;
    background: linear-gradient(to right, #8be3fc, #576bff);
    color: white;
    font-size: 16px;
    font-weight: 600;
    cursor: pointer;
    border-radius: 12px;
    box-shadow: 0 20px 30px -7px rgba(97, 118, 238, 0.5);
    transition-duration: 0.6s;
    overflow: hidden;
}

.Btn .svg-icon {
    position: absolute;
    width: 32px;
    height: 32px;
    right: 10px;
    fill: white;
    transition: right 0.7s ease-in-out;
}

.Btn .server-name {
    position: absolute;
    left: -160px;
    transition: left 0.7s ease-in-out;
}

.Btn:hover .server-name {
    left: 10px;
}

.Btn:not(.clicked):hover .initial-text {
    opacity: 0;
    transition: opacity 0.7s ease-in-out;
}

.Btn.clicked .initial-text {
    opacity: 1;
}

.Btn:not(.clicked):hover .svg-icon {
    right: 5px;
}

.Btn:active {
    transform: translate(3px, 3px);
    transition-duration: 0.5s;
}

.Btn.clicked .server-name {
    left: -160px;
    transition: left 0.7s ease-in-out;
}
